SELECT a.plant_name plantName, a.building_name buildingName, a.system_name systemName, a.device_name deviceName, a.meter_code meterCode, a.tag_name tagName, a.energy_type_name energyTypeName, b.TagValue tagValue, a.Description description, b.[datatime] dataTime FROM dbo.Variable_Info a, dbo.T_MinuteData b
b.TagName = a.tag_name AND a.[disable] != 1 AND a.total_flag = 1 AND YEAR ( b.DataTime ) = YEAR ( #{queryTime} ) AND MONTH ( b.DataTime ) = MONTH ( #{queryTime} ) AND DAY ( b.DataTime ) = DAY ( #{queryTime} ) AND DATENAME(HOUR, b.DataTime) = DATENAME(HOUR, #{queryTime} ) AND b.DataTime = ( SELECT MAX ( c.DataTime ) FROM dbo.T_MinuteData c WHERE b.tagname = c.tagname AND YEAR ( c.DataTime ) = YEAR ( #{queryTime} ) AND MONTH ( c.DataTime ) = MONTH ( #{queryTime} ) AND DAY ( c.DataTime ) = DAY ( #{queryTime} ) AND DATENAME(HOUR, c.DataTime) = DATENAME(HOUR, #{queryTime} ) )
AND a.energy_type_id = #{energyTypeId, jdbcType=INTEGER}
AND a.system_id = #{systemId, jdbcType=INTEGER}
AND a.company_id = #{companyId, jdbcType=INTEGER}
AND a.plant_id = #{plantId, jdbcType=INTEGER}
