SELECT COUNT ( 0 ) FROM ( SELECT top 100 percent M.plant_id plantId, M.plant_name plantName, M.tag_name tagName, M.building_name buildingName, N.pre preMonthValue, N.cur currentMonthValue FROM Variable_Info M, ( SELECT TagName, ( SELECT TagValue FROM T_DayData B WHERE A.TagName = B.TagName AND CONVERT ( VARCHAR ( 7 ), B.DataTime, 120 ) = CONVERT ( VARCHAR ( 7 ), #{queryTime}, 120 ) AND B.DataTime = ( SELECT MAX ( C.DataTime ) FROM dbo.T_Daydata C WHERE B.tagname = C.tagname ) ) cur, ( SELECT TagValue FROM T_DayData D WHERE A.TagName = D.TagName AND CONVERT ( VARCHAR ( 7 ), D.DataTime, 120 ) = CONVERT ( VARCHAR ( 7 ), DATEADD( MONTH, - 1, #{queryTime} ), 120 ) AND D.DataTime = ( SELECT MAX ( E.DataTime ) FROM dbo.T_Daydata E WHERE D.tagname = E.tagname AND CONVERT ( VARCHAR ( 7 ), E.DataTime, 120 ) = CONVERT ( VARCHAR ( 7 ), DATEADD( MONTH, - 1, #{queryTime} ), 120 ) ) ) pre FROM T_DayData A GROUP BY TagName ) N WHERE M.tag_name = N.TagName AND M.[disable]!= 1 AND M.total_flag = 1 AND M.energy_type_name = '水'
AND M.plant_id = #{plantId, jdbcType=INTEGER}
ORDER BY building_name ASC ) tmp_count

SELECT M.plant_id plantId, M.plant_name plantName, M.tag_name tagName, M.building_name buildingName, N.pre preMonthValue, N.cur currentMonthValue FROM Variable_Info M, ( SELECT TagName, ( SELECT TagValue FROM T_DayData B WHERE A.TagName = B.TagName AND CONVERT ( VARCHAR ( 7 ), B.DataTime, 120 ) = CONVERT ( VARCHAR ( 7 ), #{queryTime}, 120 ) AND B.DataTime = ( SELECT MAX ( C.DataTime ) FROM dbo.T_Daydata C WHERE B.tagname = C.tagname AND CONVERT ( VARCHAR ( 7 ), B.DataTime, 120 ) = CONVERT ( VARCHAR ( 7 ), #{queryTime}, 120 )) ) cur, ( SELECT TagValue FROM T_DayData D WHERE A.TagName = D.TagName AND CONVERT ( VARCHAR ( 7 ), D.DataTime, 120 ) = CONVERT ( VARCHAR ( 7 ), DATEADD( MONTH, - 1, #{queryTime} ), 120 ) AND D.DataTime = ( SELECT MAX ( E.DataTime ) FROM dbo.T_Daydata E WHERE D.tagname = E.tagname AND CONVERT ( VARCHAR ( 7 ), E.DataTime, 120 ) = CONVERT ( VARCHAR ( 7 ), DATEADD( MONTH, - 1, #{queryTime} ), 120 ) ) ) pre FROM T_DayData A GROUP BY TagName ) N WHERE M.tag_name = N.TagName AND M.[disable]!= 1 AND M.total_flag = 1 AND M.energy_type_name = '水'
AND M.plant_id = #{plantId, jdbcType=INTEGER}
ORDER BY building_name ASC

