SELECT
distinct
a.plant_name plantName,
a.building_name buildingName,
a.system_name systemName,
a.device_name deviceName,
a.meter_code meterCode,
a.tag_name tagName,
a.energy_type_name energyTypeName,
b.TagValue tagValue,
a.Description description,
b.[datatime] dataTime,
DATEADD( SECOND, 1, b.[datatime] ) showTime
FROM
dbo.consumption_group_info a,
${tableName} b

    b.TagName = a.tag_name
    AND a.[disable] = 0

        AND dataTime >= #{beginTime}


        AND dataTime <= #{endTime}


        AND a.energy_type_id = #{energyTypeId, jdbcType=INTEGER}


        AND a.system_id = #{systemId, jdbcType=INTEGER}


        AND a.company_id = #{companyId, jdbcType=INTEGER}


        AND a.plant_id = #{plantId, jdbcType=INTEGER}


        AND a.building_id in

            #{item}



        AND a.device_id in

            #{item}



        AND a.meter_id in

            #{item}



        AND A.device_type = #{deviceType, jdbcType=INTEGER}


        AND A.measure_level = #{measureLevel, jdbcType=INTEGER}


order by showTime asc, plant_id asc, buildingName asc

SELECT MAX(A.building_name) buildingName, tag_name tagName, MAX (meter_param) meterParam, MAX(plant_id) plant_id, MAX(A.plant_name) plantName, MAX(meter_code) meter_code, max(CASE WHEN CONVERT ( VARCHAR ( 10 ), B.DataTime , 120 ) = CONVERT ( VARCHAR ( 10 ), #{queryTime}, 120 ) THEN TagValue ELSE 0 END) currentTimeValue, max(CASE WHEN CONVERT ( VARCHAR ( 10 ), B.DataTime , 120 ) = CONVERT ( VARCHAR ( 10 ), DATEADD( day, - 1, #{queryTime} ), 120 ) THEN TagValue ELSE 0 END) preTimeValue FROM consumption_group_info A LEFT JOIN T_DayData B ON A.tag_name = B.TagName AND ( CONVERT ( VARCHAR ( 10 ), B.DataTime, 120 ) = CONVERT ( VARCHAR ( 10 ), #{ queryTime }, 120 ) OR CONVERT ( VARCHAR ( 10 ), B.DataTime, 120 ) = CONVERT ( VARCHAR ( 10 ), DATEADD( DAY, - 1, #{ queryTime } ), 120 ) ) WHERE energy_type_name = '水' AND A.[disable] = 0
AND A.plant_id = #{plantId, jdbcType=INTEGER}
AND A.device_type = #{deviceType, jdbcType=INTEGER}
AND A.measure_level = #{measureLevel, jdbcType=INTEGER}
GROUP BY tag_name ORDER BY plant_id ASC , buildingName ASC, meter_code ASC

