SELECT building_id, buildingName, ( currentWaterSumValue - preWaterSumValue ) currentWaterSumValue, ( currentElectricitySumValue - preElectricitySumValue ) currentElectricitySumValue, ( currentAirSumValue - preAirSumValue ) currentAirSumValue, ( currentSteamSumValue - preSteamSumValue ) currentSteamSumValue FROM ( SELECT ( SELECT DISTINCT building_name FROM consumption_group_info C WHERE C.building_id = A.building_id ) buildingName, ( SELECT DISTINCT plant_name FROM consumption_group_info C WHERE C.building_id = A.building_id ) plantName, building_id, SUM ( CASE WHEN CONVERT ( VARCHAR ( 4 ), B.DataTime , 120 ) = CONVERT ( VARCHAR ( 4 ), getdate(), 120 ) AND energy_type_name = '电' THEN TagValue ELSE 0.0 END ) currentElectricitySumValue, SUM ( CASE WHEN CONVERT ( VARCHAR ( 4 ), B.DataTime , 120 ) = CONVERT ( VARCHAR ( 4 ), DATEADD( year, - 1, getdate() ), 120 ) AND energy_type_name = '电' THEN TagValue ELSE 0.0 END ) preElectricitySumValue, SUM ( CASE WHEN CONVERT ( VARCHAR ( 4 ), B.DataTime , 120 ) = CONVERT ( VARCHAR ( 4 ), getdate(), 120 ) AND energy_type_name = '水' THEN TagValue ELSE 0.0 END ) currentWaterSumValue, SUM ( CASE WHEN CONVERT ( VARCHAR ( 4 ), B.DataTime , 120 ) = CONVERT ( VARCHAR ( 4 ), DATEADD( year, - 1, getdate() ), 120 ) AND energy_type_name = '水' THEN TagValue ELSE 0.0 END ) preWaterSumValue, SUM ( CASE WHEN CONVERT ( VARCHAR ( 4 ), B.DataTime , 120 ) = CONVERT ( VARCHAR ( 4 ), getdate(), 120 ) AND energy_type_name = '空气' THEN TagValue ELSE 0.0 END ) currentAirSumValue, SUM ( CASE WHEN CONVERT ( VARCHAR ( 4 ), B.DataTime , 120 ) = CONVERT ( VARCHAR ( 4 ), DATEADD( year, - 1, getdate() ), 120 ) AND energy_type_name = '空气' THEN TagValue ELSE 0.0 END ) preAirSumValue, SUM ( CASE WHEN CONVERT ( VARCHAR ( 4 ), B.DataTime , 120 ) = CONVERT ( VARCHAR ( 4 ), getdate(), 120 ) AND energy_type_name = '蒸汽' THEN TagValue ELSE 0.0 END ) currentSteamSumValue, SUM ( CASE WHEN CONVERT ( VARCHAR ( 4 ), B.DataTime , 120 ) = CONVERT ( VARCHAR ( 4 ), DATEADD( year, - 1, getdate() ), 120 ) AND energy_type_name = '蒸汽' THEN TagValue ELSE 0.0 END ) preSteamSumValue FROM consumption_group_info A LEFT JOIN T_YearData B ON A.tag_name = B.TagName and ( CONVERT ( VARCHAR ( 4 ), B.DataTime , 120 ) = CONVERT ( VARCHAR ( 4 ), DATEADD( year, - 1, getdate() ), 120 ) or CONVERT ( VARCHAR ( 4 ), B.DataTime , 120 ) = CONVERT ( VARCHAR ( 4 ), getdate(), 120 ) ) WHERE A.[disable] = 0
AND building_id = #{buildingId, jdbcType=INTEGER}
GROUP BY building_id) T

