erSumValue, SUM ( CASE WHEN CONVERT ( VARCHAR ( 7 ), B.DataTime , 120 ) = CONVERT ( VARCHAR ( 7 ), getdate( ), 120 ) AND energy_type_name = '空气' THEN TagValue ELSE 0.0 END ) currentAirSumValue, SUM ( CASE WHEN CONVERT ( VARCHAR ( 7 ), B.DataTime , 120 ) = CONVERT ( VARCHAR ( 7 ), DATEADD( MONTH, - 1, getdate( ) ), 120 ) AND energy_type_name = '空气' THEN TagValue ELSE 0.0 END ) preAirSumValue, SUM ( CASE WHEN CONVERT ( VARCHAR ( 7 ), B.DataTime , 120 ) = CONVERT ( VARCHAR ( 7 ), DATEADD( MONTH, - 2, getdate( ) ), 120 ) AND energy_type_name = '空气' THEN TagValue ELSE 0.0 END ) twoPreAirSumValue, SUM ( CASE WHEN CONVERT ( VARCHAR ( 7 ), B.DataTime , 120 ) = CONVERT ( VARCHAR ( 7 ), getdate( ), 120 ) AND energy_type_name = '蒸汽' THEN TagValue ELSE 0.0 END ) currentSteamSumValue, SUM ( CASE WHEN CONVERT ( VARCHAR ( 7 ), B.DataTime , 120 ) = CONVERT ( VARCHAR ( 7 ), DATEADD( MONTH, - 1, getdate( ) ), 120 ) AND energy_type_name = '蒸汽' THEN TagValue ELSE 0.0 END ) preSteamSumValue, SUM ( CASE WHEN CONVERT ( VARCHAR ( 7 ), B.DataTime , 120 ) = CONVERT ( VARCHAR ( 7 ), DATEADD( MONTH, - 2, getdate( ) ), 120 ) AND energy_type_name = '蒸汽' THEN TagValue ELSE 0.0 END ) twoPreSteamSumValue FROM consumption_group_info A LEFT JOIN T_MonthData B ON A.tag_name = B.TagName AND ( CONVERT ( VARCHAR ( 7 ), B.DataTime , 120 ) = CONVERT ( VARCHAR ( 7 ), DATEADD( MONTH, - 1, getdate( ) ), 120 ) OR CONVERT ( VARCHAR ( 7 ), B.DataTime , 120 ) = CONVERT ( VARCHAR ( 7 ), getdate( ), 120 ) ) WHERE A.[disable] = 0
AND building_id = #{buildingId, jdbcType=INTEGER}
GROUP BY building_id ) T

